DROP TABLE IF EXISTS `sys_user`;
CREATE TABLE `sys_user` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '用户 ID',
  `is_article` varchar(150) NOT NULL COMMENT '与博客关联',
  `username` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '系统登录名',
  `password` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '登录密码，加密存储, admin/1234',
  `is_img` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '头像',
  `is_permission` tinyint DEFAULT '1' COMMENT '权限字段0/管理员，1/普通用户',
  `is_account_non_expired` tinyint DEFAULT '1' COMMENT '帐户是否过期(1 未过期，0已过期)',
  `is_account_non_locked` tinyint DEFAULT '1' COMMENT '帐户是否被锁定(1 未过期，0已过期)',
  `is_credentials_non_expired` tinyint DEFAULT '1' COMMENT '密码是否过期(1 未过期，0已过期)',
  `is_enabled` tinyint(1) DEFAULT '1' COMMENT '帐户是否可用(1 可用，0 删除用户)',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `username` (`username`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='用户表';

